package org.svaor.tutorial.leetcode.kotlin.p0001_0100.p0005

import org.hamcrest.MatcherAssert.assertThat
import org.hamcrest.Matchers.anyOf
import org.hamcrest.Matchers.equalTo
import org.junit.jupiter.api.Test

internal class LongestPalindromicSubstringTest {
    @Test
    fun longestPalindrome() {
        val l = LongestPalindromicSubstring()

        assertThat(l.longestPalindrome("babad"), anyOf(equalTo("aba"), equalTo("bab")))
        assertThat(l.longestPalindrome("cbbd"), equalTo("bb"))
        assertThat(l.longestPalindrome("a32123b"), equalTo("32123"))
        assertThat(l.longestPalindrome("a321123b"), equalTo("321123"))
        assertThat(l.longestPalindrome("bb"), equalTo("bb"))
        assertThat(l.longestPalindrome(
            ""),
            equalTo("")
        )
        assertThat(l.longestPalindrome(
            ""),
            equalTo("")
        )
    }
}
